import {
  Button,
  Card,
  CardActions,
  CardContent,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Divider,
  FormControl,
  Grid,
  InputBase,
  InputLabel,
  MenuItem,
  Select,
  Slide,
  Slider,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  TextField,
  Typography,
  fade,
  makeStyles,
} from "@material-ui/core";
import React, { Fragment, useEffect, useState } from "react";

import Autocomplete from "@material-ui/lab/Autocomplete";
import autocompleteData from "../../files/search_plast_data.json";
import colors from "../../variables/colors";
import materialData from "../../files/plast.json";

const useStyles = makeStyles((theme) => ({
  filterContainer: {
    paddingLeft: theme.spacing(4),
    paddingRight: theme.spacing(4),
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  gridContainer: {
    padding: theme.spacing(8),
  },
  inputInput: {
    [theme.breakpoints.up("md")]: {
      width: "100%",
    },
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create("width"),
    width: "100%",
  },
  inputRoot: {
    color: "inherit",
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  search: {
    [theme.breakpoints.up("sm")]: {
      marginLeft: theme.spacing(3),
      width: "auto",
    },
    "&:hover": {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    backgroundColor: fade(theme.palette.common.white, 0.15),
    borderRadius: theme.shape.borderRadius,
    marginLeft: 0,
    marginRight: theme.spacing(2),
    position: "relative",
    width: "100%",
  },
  searchIcon: {
    alignItems: "center",
    display: "flex",
    height: "100%",
    justifyContent: "center",
    padding: theme.spacing(0, 2),
    pointerEvents: "none",
    position: "absolute",
  },
}));

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

const Result = (props) => {
  const [dialogOpen, setDialogOpen] = useState(false);
  const { data, filter } = props;

  const handleClose = () => {
    setDialogOpen(false);
    console.log(filter);
    console.log(data);
  };

  const setNewMaterial = () => {
    const req = {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        newMaterial: data,
      }),
    };

    fetch("/results", req)
      .then((res) => res.json())
      .then((json) => console.log(json));
  };

  return (
    <Fragment>
      <Dialog
        open={dialogOpen}
        TransitionComponent={Transition}
        keepMounted
        onClose={handleClose}
        aria-labelledby="alert-dialog-slide-title"
        aria-describedby="alert-dialog-slide-description"
      >
        <DialogTitle id="alert-dialog-slide-title">{data.mat_desc}</DialogTitle>
        <DialogContent>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Key</TableCell>
                <TableCell>Value</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {Object.keys(filter)
                // .filter((e) => e !== "search")
                .map((e) => {
                  // console.log(e);
                  return (
                    <TableRow>
                      <TableCell>
                        <Typography>{e}</Typography>
                      </TableCell>
                      <TableCell>
                        <Typography>{data[e] ? data[e] : "Nicht verfügbar"}</Typography>
                      </TableCell>
                    </TableRow>
                  );
                })}
            </TableBody>
          </Table>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Schließen
          </Button>
        </DialogActions>
      </Dialog>
      <Grid item xs={12} md={6} lg={4} xl={3}>
        <Card>
          <CardContent>
            <Typography color="textSecondary" gutterBottom>
              {data.id}
            </Typography>
            <Typography variant="body2" component="p">
              {data.mat_desc}
            </Typography>
            <Typography variant="body2" component="p">
              {data.campus_fam}
            </Typography>
            <Typography variant="body2" component="p">
              {data.producer}
            </Typography>
          </CardContent>
          <CardActions>
            <Button size="small" variant="outlined" onClick={() => setDialogOpen(true)}>
              <Typography variant="body2">Mehr erfahren</Typography>
            </Button>
            <Button
              size="small"
              variant="outlined"
              onClick={setNewMaterial}
              style={{ backgroundColor: colors.lightGreen, color: colors.lightGrey }}
            >
              <Typography variant="body">Auswählen</Typography>
            </Button>
          </CardActions>
        </Card>
      </Grid>{" "}
    </Fragment>
  );
};

const SearchField = (props) => {
  return (
    <Grid item xs={12}>
      {/* <Autocomplete
        id="combo-box-demo"
        options={top100Films}
        getOptionLabel={(option) => option}
        style={{ width: 300 }}
        renderInput={(params) => (
          <TextField
            id="filled-name"
            label={props.placeholder}
            // value={props.value}
            // onChange={props.onChange}
            variant="filled"
            fullWidth
          />
        )}
      /> */}

      <Autocomplete
        options={props.options}
        getOptionLabel={(option) => option}
        value={props.value}
        onChange={(event, newValue) => {
          props.onChange(newValue);
        }}
        renderInput={(params) => <TextField {...params} label={props.placeholder} margin="normal" />}
      />

      {/* <TextField label={props.placeholder} margin="normal" value={props.value} onChange={props.onChange} /> */}
    </Grid>
  );
};

const Filter = (props) => {
  const [dropdownKeys, setDropdownKeys] = useState([]);
  const [sliderValue, setSliderValue] = useState([0, 10]);
  const [sliderRange, setSliderRange] = useState([0, 10]);
  const [dropdownSelection, setDropdownSelection] = useState("");
  const [data, setData] = useState({});
  const [loaded, setLoaded] = useState(false);

  useEffect(() => {
    // get dropdown options and slider range
    fetch(`/search/${props.suffix.toLowerCase()}`)
      .then((res) => res.json())
      .then((json) => {
        setDropdownKeys(Object.keys(json).map((elem) => elem));
        setData(json);
      })
      .then(() => setLoaded(true));
  }, []);

  // selecting first element by default
  // useEffect(() => {
  //   setDropdownSelection(dropdownKeys[0]);
  // }, [dropdownKeys]);

  useEffect(() => {
    if (loaded) {
      setSliderRange([data[dropdownSelection].min, data[dropdownSelection].max]);
      setSliderValue([data[dropdownSelection].min, data[dropdownSelection].max]);
    }
  }, [dropdownSelection]);

  const handleDropdownChange = (e) => {
    setDropdownSelection(e.target.value);
    props.handleDropdownChange(e.target.value);
  };

  const handleSliderChange = (e, newValue) => {
    setSliderValue(newValue);
    props.handleSliderChange(newValue); // makes the component really laggy
