import {
  ColumnChooser,
  Grid as GridDevExpress,
  Table,
  TableColumnVisibility,
  TableHeaderRow,
  TableTreeColumn,
  Toolbar,
} from "@devexpress/dx-react-grid-material-ui";
import {
  CustomTreeData,
  DataTypeProvider,
  IntegratedSorting,
  SelectionState,
  SortingState,
  TreeDataState,
} from "@devexpress/dx-react-grid";
import { useContext, useEffect, useState } from "react";

import EvaluationDialog from "./EvaluationDialog.jsx";
import AddBomDialog from "./AddBomDialog.jsx";
import AddMaterialDialog from "./AddMaterialDialog.jsx";
import AddPlastToMat from "./AddPlastToMat.jsx";
import Autocomplete from "@material-ui/lab/Autocomplete";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import DeleteMaterialDialog from "./DeleteMaterialDialog.jsx";
import CsvUploadDialog from "./CsvUploadDialog.jsx";
import FiberManualRecordIcon from "@material-ui/icons/FiberManualRecord";
import { FormControl } from "@material-ui/core";
import Grid from "@material-ui/core/Grid";
import Input from "@material-ui/core/Input";
import { MainContext } from "./MainContext.jsx";
import MenuItem from "@material-ui/core/MenuItem";
import PolymerIcon from "@material-ui/icons/Polymer";
import React from "react";
import { SearchDialog } from "../../components/Search/SearchDialog.jsx";
import Select from "@material-ui/core/Select";
import TextField from "@material-ui/core/TextField";
import Tooltip from "@material-ui/core/Tooltip";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import WarningIcon from "@material-ui/icons/Warning";

const useStyles = makeStyles((theme) => ({
  mainwindow: {
    padding: 20,
    minHeight: "calc(100vh - 115px - 100px)",
    height: "100%",
    paddingBottom: 0,
    textAlign: "left",
  },
  divider: {
    margin: 0,
    marginTop: 15,
  },
  progress: {
    textAlign: "center",
    justifyContent: "center",
    padding: 15,
  },
  buttons: {
    borderColor: "#005000",
    color: "#005000",
    textTransform: "none",
    margin: 20,
    height: 30,
    width: 120,
  },
  label: { marginTop: 10 },
  buttonbox: {
    textAlign: "right",
    paddingTop: 15,
    paddingBottom: 15,
  },
  textfield: { margin: 0, padding: 0, background: "white" },
  root_card: {
    marginTop: 15,
    marginBottom: 0,
  },
  title: {
    fontSize: 13,
  },
  card_div: {
    marginBottom: 15,
  },
  formControl: {
    minWidth: 325,
  },
}));

const getChildRows = (row, rootRows) => {
  const childRows = rootRows.filter(
    (r) => r.parent_id === (row ? row.result_id : null)
  );
  return childRows.length ? childRows : null;
};

const RecyclingTypeProviderFormatter = ({ value, row }) => {
  if (value > 0.9 && value <= 1)
    return (
      <span>
        <Tooltip
          title={
            <Typography variant="body1">
              Diese Komponente besitzt einen Recyclingwert zwischen 0,9 und 1,0.
            </Typography>
          }
        >
          <FiberManualRecordIcon
            style={{ color: "green" }}
            fontSize="large"
          ></FiberManualRecordIcon>
        </Tooltip>
      </span>
    );
  else if (value > 0.6 && value <= 0.9)
    return (
      <span>
        <Tooltip
          title={
            <Typography variant="body1">
              Diese Komponente besitzt einen Recyclingwert zwischen 0,6 und 0,9.
              VerÃ¤ndern Sie die darunter liegenden Komponenten, um den
              Recyclingwert zu erhÃ¶hen.
            </Typography>
          }
        >
          <FiberManualRecordIcon
            style={{ color: "yellow" }}
            fontSize="large"
          ></FiberManualRecordIcon>
        </Tooltip>
      </span>
    );
  else if (value > 0.3 && value <= 0.6)
    return (
      <span>
        <Tooltip
          title={
            <Typography variant="body1">
              Diese Komponente besitzt einen Recyclingwert zwischen 0,3 und 0,6.
              VerÃ¤ndern Sie die darunter liegenden Komponenten, um den
              Recyclingwert zu erhÃ¶hen.
            </Typography>
          }
        >
          <FiberManualRecordIcon
            style={{ color: "orange" }}
            fontSize="large"
          ></FiberManualRecordIcon>
        </Tooltip>
      </span>
    );
  else if (value > 0.0 && value <= 0.3)
    return (
      <span>
        <Tooltip
          title={
            <Typography variant="body1">
              Diese Komponente besitzt einen Recyclingwert zwischen 0,0 und 0,3.
              VerÃ¤ndern Sie die darunter liegenden Komponenten, um den
              Recyclingwert zu erhÃ¶hen.
            </Typography>
          }
        >
          <FiberManualRecordIcon
            style={{ color: "red" }}
            fontSize="large"
          ></FiberManualRecordIcon>
        </Tooltip>
      </span>
    );
  return (
    <span>
      <Tooltip
        title={
          <Typography variant="body1">
            Diese Komponente besitzt keine eigenen Recyclingwert, da sie nicht
            aus mehreren Komponenten besteht.
          </Typography>
        }
      >
        <FiberManualRecordIcon
          style={{ color: "lightgrey" }}
          fontSize="large"
        ></FiberManualRecordIcon>
      </Tooltip>
    </span>
  );
};

const RecyclingTypeProvider = (props) => (
  <DataTypeProvider
    formatterComponent={RecyclingTypeProviderFormatter}
    {...props}
  />
);

const PlasticTypeProviderFormatter = ({ value, row }) => {
  if (value == 1)
    return (
      <span>
        <Tooltip
          title={
            <Typography variant="body1">
              Dieser Komponente kann ein Kunststoff zugeordnet werden.
            </Typography>
          }
        >
          <PolymerIcon style={{ color: "black" }}></PolymerIcon>
        </Tooltip>
      </span>
    );
  else
    return (
      <span>
        <Tooltip
          title={
            <Typography variant="body1">
              Dieser Komponente kann kein Kunststoff zugeordnet werden.
            </Typography>
          }
        >
          <PolymerIcon style={{ color: "lightgrey" }}></PolymerIcon>
        </Tooltip>
      </span>
    );
};

const PlasticTypeProvider = (props) => (
  <DataTypeProvider
    formatterComponent={PlasticTypeProviderFormatter}
    {...props}
  />
);

const TableTree = () => {
  const {
    selected_material,
    parent_material,
    bom_updated,
    delete_material,
    selected_construction_id,
    selected_construction_title,
    child_updated,
    evaluation_open,
    data_backend,
    highest_level_id,
    new_bom_created,
    selection_atomic,
  } = useContext(MainContext);

  const [evaluationOpen, setEvaluationOpen] = evaluation_open;
  const [selectedMaterial, setSelectedMaterial] = selected_material;
  const [parentMaterial, setParentMaterial] = parent_material;
  const [deleteMaterial, setDeleteMaterial] = delete_material;
  const [bomUpdated, setBomUpdated] = bom_updated;
  const [
    selectedConstructionId,
    setSelectedConstructionId,
  ] = selected_construction_id;
  const [
    selectedConstructionTitle,
    setSelectedConstructionTitle,
  ] = selected_construction_title;
  const [bomMaterialId, setBomMaterialId] = useState("");

  const classes = useStyles();
  const [dataBackend, setDataBackend] = data_backend;
  const [childUpdated, setChildUpdated] = child_updated;
  const [highestLevelId, setHighestLevelId] = highest_level_id;
  const [newBomCreated, setNewBomCreated] = new_bom_created;
  const [selectionAtomic, setSelectionAtomic] = selection_atomic;

  useEffect(() => {
    if (selectedConstructionTitle !== "Bitte auswaehlen") {
      fetch("http://localhost:5000/tree/" + bomMaterialId)
        .then((res) => {
          return res.json();
        })
        .then((d) => {
          console.log(d);
          setDataBackend(d);
          console.log(dataBackend);
          setBomUpdated(false);
          setRowSelection([]);
        });
    } else {
    }
  }, [bomUpdated, bomMaterialId]);

  useEffect(() => {
    if (selectedConstructionTitle !== "Bitte auswaehlen") {
      fetch("http://localhost:5000/mat?cons_id=" + selectedConstructionId)
        .then((res) => {
          return res.json();
        })
        .then((d) => {
          transformDropdownData(d);
        });
    } else {
    }
  }, [selectedConstructionId, newBomCreated]);

  const [columns] = useState([
    { name: "result_id", title: "Id" },
    { name: "parent_id", title: "Parent" },
    { name: "mat_id", title: "Mat.Nr." },
    { name: "bom_id", title: "Id. BOM Eintrag" },
    { name: "mat_desc", title: "Mat.Bez." },
    { name: "level", title: "Ebene" },
    { name: "mat_rw", title: "Recycl." },
    { name: "mat_id_int", title: "ERP Mat.Nr." },
    { name: "mat_desc_int", title: "ERP Bez." },
    { name: "cad_id", title: "CAD Nr." },
    { name: "is_atomic", title: " " },
    { name: "mara_plast_id", title: "Kunststoff_ID" },
    { name: "plast_desc", title: "Kunstst.Bez." },
    { name: "plast_fam", title: "Kunstst.Fam." },
    { name: "height", title: "HÃ¶he" },
    { name: "width", title: "Breite" },
    { name: "depth", title: "Tiefe" },
    { name: "unit", title: "Einheit" },
    { name: "weight", title: "Gewicht" },
    { name: "weight_unit", title: "G. Einheit" },
    { name: "volume", title: "Volumen" },
    { name: "volume_unit", title: "Vol. Einheit" },
    { name: "orga_id", title: "Organisations Id" },
  ]);

  const [tableColumnExtensions] = useState([
    { columnName: "mat_id", width: 250 },
    { columnName: "mat_desc", width: 250 },
    { columnName: "plast_desc", width: 250 },
    { columnName: "is_atomic", width: 50 },
  ]);
  const [defaultExpandedRowIds] = useState([0]);
  const [defaultHiddenColumnNames] = useState([
    "level",
    "result_id",
    "parent_id",
    "mat_id_int",
    "mat_desc_int",
    "cad_id",
    "mara_plast_id",
    "orga_id",
    //"bom_id",
  ]);

  const [rowSelection, setRowSelection] = useState([]);
  const [selectedMaraId, setSelectedMaraId] = useState(0);

  const [listDropdownData, setListDropdownData] = useState([]);
  const [dropdownSelected, setDropdownSelected] = useState([]);

  const setParent = () => {
    if (rowSelection.length == 1) {
      setParentMaterial(dataBackend[rowSelection].mat_id);
      setHighestLevelId(dataBackend[rowSelection].result_id);
      setSelectionAtomic(dataBackend[rowSelection].is_atomic);
    }
  };

  const handleDropdownChange = (event) => {
    setDropdownSelected(event.target.textContent);
    setBomMaterialId(parseInt(event.target.textContent.split(" ")[0]));
  };

  const transformDropdownData = (data) => {
    if (data !== null && data.length !== 0) {
      const source = data.map((item) => item.id + " " + item.mat_desc);
      setListDropdownData(source);
    } else setListDropdownData([]);
  };

  const setDeleteComponent = () => {
    if (rowSelection.length == 1) {
      setDeleteMaterial(dataBackend[rowSelection].bom_id);
    }
  };

  const [searchDialogOpen, setSearchDialogOpen] = useState(false);

  const handleSearchDialogClose = () => {
    setSearchDialogOpen(false);
  };

  useEffect(() => {
    console.log("search dialog changed", searchDialogOpen);
  }, [searchDialogOpen]);

  return (
    <div>
      <SearchDialog
        open={searchDialogOpen}
        handleSearchDialogClose={handleSearchDialogClose}
        // selectedId={[1,2,3,4,5]} // used to be able to make the post/update request within each search result
      />
      <Grid
        container
        item
        xs={12}
        style={{
          background: "white",
          marginTop: 25,
          textAlign: "left",
        }}
      >
        <Grid item xs={6}>
          <Typography>Bitte wÃ¤hlen Sie eine StÃ¼ckliste aus.</Typography>
          <FormControl className={classes.formControl}>
            <Autocomplete
              disableClearable
              value={dropdownSelected}
              onChange={handleDropdownChange}
              options={listDropdownData}
              renderInput={(params) => (
                <TextField
                  {...params}
                  value={dropdownSelected}
                  margin="dense"
                  className={classes.textfield}
                />
              )}
            />
          </FormControl>
        </Grid>
        <Grid item xs={2}>
          <Tooltip
            title={
              <Typography variant="body1">
                Hiermit kÃ¶nnen Sie eine neue StÃ¼ckliste als csv-Datei
                importieren.
              </Typography>
            }
          >
            <div>
              <CsvUploadDialog></CsvUploadDialog>
            </div>
          </Tooltip>
        </Grid>

        <Grid item xs={2}>
          <Tooltip
            title={
              <Typography variant="body1">
                Hiermit kÃ¶nnen Sie eine neue StÃ¼ckliste erstellen.
              </Typography>
            }
          >
            <div>
              <AddBomDialog></AddBomDialog>
            </div>
          </Tooltip>
        </Grid>

        <Grid item xs={2}>
          <Tooltip
            title={
              <Typography variant="body1">
                Hiermit kÃ¶nnen Sie die gewÃ¤hlte StÃ¼ckliste bewerten.
              </Typography>
            }
          >
            <div>
              <EvaluationDialog></EvaluationDialog>
            </div>
          </Tooltip>
        </Grid>
      </Grid>
      <div>
        <GridDevExpress rows={dataBackend} columns={columns}>
          <SelectionState
            selection={rowSelection}
            onSelectionChange={setRowSelection}
          />
          <TreeDataState defaultExpandedRowIds={defaultExpandedRowIds} />
          <SortingState />
          <CustomTreeData getChildRows={getChildRows} />
          <IntegratedSorting />
          <RecyclingTypeProvider for={["mat_rw"]} />
          <PlasticTypeProvider for={["is_atomic"]} />
          <Table columnExtensions={tableColumnExtensions} />
          <TableColumnVisibility
            defaultHiddenColumnNames={defaultHiddenColumnNames}
          />
          <TableHeaderRow showSortingControls />
          <TableTreeColumn for="mat_id" showSelectionControls />
          <Toolbar />
          <ColumnChooser />
        </GridDevExpress>
      </div>

      <Grid
        container
        item
        xs={12}
        style={{
          background: "white",
          marginTop: 25,
          marginBottom: 25,
        }}
      >
        <Grid item xs={6}></Grid>

        <Grid item xs={2}>
          <Tooltip
            title={
              <Typography variant="body1">
                Hiermit kÃ¶nnen Sie eine ausgewÃ¤hlte Komponente lÃ¶schen.
              </Typography>
            }
          >
            <div onClick={setDeleteComponent}>
              <DeleteMaterialDialog></DeleteMaterialDialog>
            </div>
          </Tooltip>
        </Grid>

        <Grid item xs={2}>
          <Tooltip
            title={
              <Typography variant="body1">
                Hiermit kÃ¶nnen Sie einer bestehenden Komponente eine weitere
                Komponente hinzufÃ¼gen.
              </Typography>
            }
          >
            <div onClick={setParent}>
              <AddMaterialDialog></AddMaterialDialog>
            </div>
          </Tooltip>
        </Grid>
        <Grid item xs={2}>
          <Tooltip
            title={
              <Typography variant="body1">
                Hiermit kÃ¶nnen Sie einer bestehenden Komponente einen Kunststoff
                zuordnen.
              </Typography>
            }
          >
            <Button
              className={classes.buttons}
              onClick={() => {
                setParent();
                setSearchDialogOpen(!searchDialogOpen);
              }}
            >
              <PolymerIcon style={{ marginRight: 5 }}></PolymerIcon>
              Zuordnen
            </Button>
            {/* <div onClick={setParent}>
              <AddPlastToMat></AddPlastToMat>
            </div> */}
          </Tooltip>
        </Grid>
      </Grid>
      <Grid container item xs={12} justify="center">
        {(rowSelection.length == 1) == false &&
        selectedConstructionTitle != "Bitte auswaehlen" &&
        bomMaterialId != "" ? (
          <div style={{ color: "red", marginTop: 20, marginBottom: 20 }}>
            <WarningIcon
              style={{ fontSize: "small", marginRight: 10 }}
            ></WarningIcon>{" "}
            Bitte wÃ¤hlen Sie eine Komponente aus.
          </div>
        ) : (
          <div />
        )}
      </Grid>
    </div>
  );
};

export default TableTree;
